package com.epcentre.cache;


public class PhoneClient  extends NetObject {
	
	private String  epCode; 
	
	private int epGunNo;

	private int accountId;
	
	private String account;
	
	private String ip;
	
	public PhoneClient()
	{
		epCode=""; 	
		epGunNo =0;
		accountId =0;
		
		account="";
		
	}
	
	public String getEpCode() {
		return epCode;
	}
	public void setEpCode(String epCode) {
		this.epCode = epCode;
	}
	
	public int getStatus() {
		return status;
	}
	public void setStatus(int status) {
		this.status = status;
	}
	
	
	public int getAccountId() {
		return accountId;
	}
	public void setAccountId(int accountId) {
		this.accountId = accountId;
	}
	public String getAccount() {
		return account;
	}
	public void setAccount(String account) {
		this.account = account;
	}
	public void setEpGunNo(int epGunNo) {
		this.epGunNo = epGunNo;
	}

	public String getIp() {
		return ip;
	}

	public void setIp(String ip) {
		this.ip = ip;
	}

	public int getEpGunNo() {
		return epGunNo;
	}
	
	@Override
	public String toString() {
		
		final StringBuilder sb = new StringBuilder();
        sb.append("PhoneClient");
        
        sb.append("{epCode=").append(epCode).append("}\n");
        sb.append(",{epGunNo=").append(epGunNo).append("}\n");
        sb.append(",{accountId=").append(accountId).append("}\n");
        
       
        sb.append(",{account=").append(account).append("}\n");
        sb.append(",{ip=").append(ip).append("}\n");
   		return sb.toString();
	}

	
}
